mapper.select:
SELECT 
    DATE_FORMAT(created_time, '%Y-%m') as month,
    COUNT(*) as order_count,
    SUM(total_amount) as total_amount
FROM orders


        AND created_time >= #{startDate}


        AND created_time <= #{endDate}


        AND status = #{status}


GROUP BY DATE_FORMAT(created_time, '%Y-%m')
ORDER BY month DESC
